mapper.update:
UPDATE BK_HISTORY SET				  
	   BK_RSVNO = #{rNum}
	  ,BK_NAME = #{MS_NAME}
	  ,BK_MSNUM = #{MS_NUM}
	  ,BK_FIRST_PHONE1 = #{MS_FIRST_PHONE1}
	  ,BK_MID_PHONE1 = hex(aes_encrypt(#{MS_MID_PHONE1}, 'JBOG'))
	  ,BK_LAST_PHONE1 = #{MS_LAST_PHONE1}
	  ,BK_YN = 'Y'
	  ,UPDATE_STAFF = DATE_FORMAT(now(), "%Y%m%d")
	  ,UPDATE_DATETIME = now()
	  ,UPDATE_IP = #{ip}
 WHERE CO_DIV = '100'
   AND BK_DAY = #{day}
   AND BK_COS = #{cos}
   AND BK_TIME = #{time}
   AND BK_YN = 'N'

mapper.select:
SELECT COUNT(*)
  FROM BK_HISTORY
 WHERE CO_DIV = '100'
   AND BK_DAY = #{day}
   AND BK_COS = #{cos}
   AND BK_TIME = #{time}
   AND BK_YN = 'N'

mapper.update:
UPDATE BK_HISTORY SET
       VIEW_ID = #{id}
      ,LIMIT_DATE = #{limitDate}
 WHERE CO_DIV = '100'
   AND BK_DAY = #{day}
   AND BK_COS = #{cos}
   AND BK_TIME = #{time}
